class Nodo {
    constructor(valor) {
        this.valor = valor;
        this.izquierda = null;
        this.derecha = null;
    }
}

function insertar(nodo, valor) {
    if (nodo === null) {
        return new Nodo(valor);
    }
    if (valor < nodo.valor) {
        nodo.izquierda = insertar(nodo.izquierda, valor);
    } else {
        nodo.derecha = insertar(nodo.derecha, valor);
    }
    return nodo;
}

function inorden(nodo) {
